function rebootStyle_btnFilter(){for(let e of btn_filter)e.classList.remove("active","active-tag")}function reloadGallery(e){for(let t of itemGallery){t.style.display="none";const l=t.children[0].dataset.galleryTag;"all"!==e&&e!==l||(t.style.display="block")}}const itemGallery=document.querySelectorAll(".item-column"),btn_filter=document.querySelectorAll(".nav-link");for(let e of btn_filter)e.addEventListener("click",()=>{rebootStyle_btnFilter(),e.classList.add("active","active-tag"),reloadGallery(e.dataset.imagesToggle)});